mapper.select:
select b.article_no, b.user_id, b.subject, b.content, b.hit, m.user_name, b.register_time from qnaboard b, members m where b.user_id = m.user_id
and b.subject like concat('%', #{word}, '%')
and ${key} = #{word}
order by b.article_no desc limit #{start}, #{spp}

select count(article_no) from qnaboard
where subject like concat('%', #{word}, '%')
where ${key} = #{word}

select memo_no, user_id, comment, memo_time, article_no 
from memo
where article_no = #{articleNo}

mapper.update:
update qnaboard
set hit = hit + 1
where article_no = #{articleNo}

update qnaboard
set subject = #{subject}, content = #{content}
where article_no = #{articleNo}

update memo
set comment = #{comment}
where memo_no = #{memo_no}
